let ResponsiveDOM={mediaQueryListeners:[],components:[],onMediaQueryChange(e,t){if(!Array.isArray(e)||!Array.isArray(t))throw Error("Queries and funcs should be arrays.");e.forEach((e,i)=>{let r=t[i];if("string"!=typeof e||"function"!=typeof r)throw Error("Invalid query or callback function.");let s=window.matchMedia(e),a=e=>{e.matches&&r()};s.addListener(a),this.mediaQueryListeners.push({mediaQueryList:s,callback:a})})},removeMediaQueryListeners(){this.mediaQueryListeners.forEach(({mediaQueryList:e,callback:t})=>{e.removeListener(t)}),this.mediaQueryListeners=[]},isMobile(){let e=window.matchMedia("(max-width: 767px)");return e.matches},isTablet(){let e=window.matchMedia("(min-width: 768px) and (max-width: 1023px)");return e.matches},isDesktop(){let e=window.matchMedia("(min-width: 1024px)");return e.matches},isTV(){let e=window.matchMedia("(min-width: 1366px)");return e.matches},createComponent(e,t){let i=document.querySelector(e);if(!i)throw Error(`Element with selector '${e}' not found.`);let r={element:i,options:t||{darkmode(){i.classList.add("rd-dark")},lightmode(){i.classList.remove("rd-dark")}}};return this.components.push(r),r}};export default ResponsiveDOM;