let ResponsiveDOM={mediaQueryListeners:[],onMediaQueryChange(e,t){if(!Array.isArray(e)||!Array.isArray(t))throw Error("Queries and funcs should be arrays.");e.forEach((e,i)=>{let a=t[i];if("string"!=typeof e||"function"!=typeof a)throw Error("Invalid query or callback function.");let r=window.matchMedia(e),s=e=>{e.matches&&a()};r.addListener(s),this.mediaQueryListeners.push({mediaQueryList:r,callback:s})})},removeMediaQueryListeners(){this.mediaQueryListeners.forEach(({mediaQueryList:e,callback:t})=>{e.removeListener(t)}),this.mediaQueryListeners=[]},isMobile(){let e=window.matchMedia("(max-width: 767px)");return e.matches},isTablet(){let e=window.matchMedia("(min-width: 768px) and (max-width: 1023px)");return e.matches},isDesktop(){let e=window.matchMedia("(min-width: 1024px)");return e.matches},isTV(){let e=window.matchMedia("(min-width: 1366px)");return e.matches}};export default ResponsiveDOM;